        var closeColumnAnimation = new TimelineMax({onComplete: function(){
          return _this.done();
        }});

        var hideColumnValue = (isLeftSide ? '-100%' : '100%');

        closeColumnAnimation
          .to('.fullscreen-bg', 1, {opacity:'1',ease: Power4.easeInOut},'close')
